definition.script:
pipeline {
    agent { label 'worker-node' } // Run the pipeline on the worker node

    environment {
        FASTAPI_PORT = "8000"
    }

    stages {
        // Stage 1: Spellcheck
        stage('Spellcheck') {
            steps {
                echo "Running spellcheck on documentation..."
                sh '''
                . /opt/jenkins-venv/bin/activate && codespell Documentation/*.md || true
                '''
            }
        }

        // Stage 2: Build and Run FastAPI
        stage('Build FastAPI') {
            steps {
                echo "Installing dependencies and running FastAPI app..."
                sh '''
                . /opt/jenkins-venv/bin/activate && pip install --upgrade pip && pip install -r Application/requirements.txt && uvicorn Application.main:app --host 0.0.0.0 --port ${FASTAPI_PORT} &
                '''
            }
        }

        // Stage 3: Deploy to K3s
        stage('Deploy to K3s') {
            steps {
                echo "Deploying FastAPI app to Kubernetes (K3s)..."
                sh '''
                export KUBECONFIG=/home/jenkins/kubeconfig
                kubectl apply -f K3s/k3s-deployment.yaml --validate=false
                kubectl get pods
                '''
            }
        }
    }

    post {
        always {
            echo "Pipeline execution completed!"
        }
    }
}
